import { useState } from "react";
import GameRow from "../game-row/game-row";
import Header from "../header/header";
import Modal from "../modal/modal";
import "./game.scss";
const Game = () => {
  const [showModal, setShowModal] = useState(false);

  const handleClose = () => {
    setShowModal(false);
  };
  const handleShowModal = () => {
    setShowModal(true);
  };

  return (
    <div id="game">
      <Header handleShowModal={handleShowModal} />
      <GameRow />
      <Modal show={showModal} handleClose={handleClose}>
        <div style={{color: "#ffffff"}}>
          <p>Welcome to BFHL Health Wordle. Guess words related to healthcare and get on the high score board. </p>
          <hr />
          <h2>Rules</h2>
          <p>Select the word size you want to play with and start guessing!</p>
          <h3>Examples</h3>
          <div>
            <div>Y</div>
            <div>O</div>
            <div>G</div>
            <div>A</div>
          </div>
        </div>
      </Modal>
    </div>
  );
};
export default Game;
