import GameTile from "../game-tile/game-tile";
import { KEY_STATE, SETTING } from "../../global/global";
import KeyBoard from "../keyboard/keyboard";
import "./game-row.scss";
import useGameRowHook from "./game-row-hook";
import { useContext, useEffect, useRef } from "react";
import { GameContext } from "../../pages/game-page";

const GameRow = () => {
  const {gameData,updateGameData} = useContext(GameContext)
  const {states, guessedWords,selectedLetters} = gameData
  useEffect(() =>{
    const initialGuessedWords = Array.from(Array(SETTING.COUNT_OF_TRY), () =>
    new Array(gameData.wordLength).fill(null)
  );
  const initialStates = Array.from(Array(SETTING.COUNT_OF_TRY), () =>
    new Array(gameData.wordLength).fill(KEY_STATE.EMPTY)
  );
  updateGameData({...gameData, states:initialStates,guessedWords: initialGuessedWords});

  },[])
  const {wordLength} = gameData
  const {
    onKeyPressed,
    pressEnter,
    pressLetter,
    pressBackspace,
    animations,
    tryStates,
    message,
  } = useGameRowHook();

  const ref: any = useRef(null);
  useEffect(() => {
    if (ref.current) {
      ref.current.focus();
    }
  });

  var rows = [];
  for (var i = 0; i < SETTING.COUNT_OF_TRY; i++) {
    var tile = [];
    for (var j = 0; j < wordLength; j++) {
      tile.push(
        <GameTile
          state={states[i][j]}
          content={guessedWords[i][j]}
          animation={animations[i][j]}
        />
      );
    }
    rows.push(
      <div className="row" data-state={tryStates[i]}>
        {tile}
      </div>
    );
  }

  return (
    <div onKeyDown={onKeyPressed} tabIndex={0} ref={ref}>
      <div id="board-container">
        <div id="board">{rows}</div>
        {message ? (
          <div className="notification" id="game-notification">
            {message}
          </div>
        ) : (
          ""
        )}
      </div>
      <KeyBoard
        selectedLetters={selectedLetters}
        pressVirualKeyBoard={pressLetter}
        pressEnter={pressEnter}
        pressBackspace={pressBackspace}
      />
    </div>
  );
};
export default GameRow;
