import { useState } from "react";
import GameRow from "../game-row/game-row";
import Header from "../header/header";
import Modal from "../modal/modal";
import "./game.scss";
const Game = () => {
  const [showModal, setShowModal] = useState(false);

  const handleClose = () => {
    setShowModal(false);
  };
  const handleShowModal = () => {
    setShowModal(true);
  };

  return (
    <div id="game">
      <Header handleShowModal={handleShowModal} />
      <GameRow />
      <Modal show={showModal} handleClose={handleClose}>
        <div>
          <p>Welcome to BFHL Health Wordle. Guess words related to healthcare and get on the high score board. </p>
          <hr />
          <h2>Rules</h2>
          <p>Here is a video about how this game works.</p>
        </div>
      </Modal>
    </div>
  );
};
export default Game;
